def frame3():      
    window = Tk()
    window.geometry("1014x620")
    window.configure(bg = "#5C66C4")
    window.title("Usage")
    
    canvas = Canvas(
        window,
        bg = "#5C66C4",
        height = 620,
        width = 1014,
        bd = 0,
        highlightthickness = 0,
        relief = "ridge"
    )

    canvas.place(x = 0, y = 0)
    canvas.create_text(
        272.0,
        39.0,
        anchor="nw",
        text="Usage\n",
        fill="#D9D9D9",
        font=("Inter Bold", 40 * -1)
    )

    # Maintenance button
    button_image_1 = PhotoImage(
        file=relative_to_assets("button_1.png"))
    button_1 = Button(
        image=button_image_1,
        borderwidth=0,
        highlightthickness=0,
        command=lambda: [window.destroy(), get_maintenance()],
        relief="flat"
    )
    button_1.place(
        x=0.0,
        y=280.0,
        width=152.0,
        height=46.0
    )

    # Menu button
    button_image_2 = PhotoImage(
        file=relative_to_assets("button_2.png"))
    button_2 = Button(
        image=button_image_2,
        borderwidth=0,
        highlightthickness=0,
        command=lambda: [window.destroy(), get_menu()],
        relief="flat"
    )
    button_2.place(
        x=0.0,
        y=130.0,
        width=152.0,
        height=46.0
    )

    # Home button
    button_image_3 = PhotoImage(
        file=relative_to_assets("button_3.png"))
    button_3 = Button(
        image=button_image_3,
        borderwidth=0,
        highlightthickness=0,
        command=lambda: [window.destroy(), get_home()],
        relief="flat"
    )
    button_3.place(
        x=0.0,
        y=205.0,
        width=152.0,
        height=46.0
    )
    
    # the figure that will contain the plots
    fig = Figure(figsize = (4, 4))
    fig2 = Figure(figsize = (4, 4))
    
    # data points
    res = []
    driver(res)
    water = [i[1] for i in res]
    electric = [i[2] for i in res]
  
    # adding the subplot
    plot1 = fig.add_subplot(1,1,1)
    plot1.set_title('Water usage')
    plot2 = fig2.add_subplot(1,1,1)
    plot2.set_title('Electricity usage')
    # plotting the graph
    plot1.plot(water)
    plot2.plot(electric)
 
    # creating the Tkinter canvas
    # containing the Matplotlib figure
    figure = FigureCanvasTkAgg(fig, master = window)  
    figure2 = FigureCanvasTkAgg(fig2, master = window)  
    figure.draw()
    figure2.draw()
    
    # placing the canvas on the Tkinter window
    # left to right : water, electric
    figure2.get_tk_widget().pack(side='right')
    figure.get_tk_widget().pack(side='right')
    

    window.resizable(False, False)
    window.mainloop()

